var eventCollection = Spark.runtimeCollection("raceEvents");

var success = eventCollection.remove({"_id" : {"$oid" : queriedEventId}});

Spark.setScriptData("success", success);

displayRaceEvent.script:
var queriedEventId = Spark.data.scriptData.eventId;

if (queriedEventId != "null")
{
    var eventCollection = Spark.runtimeCollection("raceEvents");
    var selectedEvent = eventCollection.findOne({"_id" : {$oid : queriedEventId}})
    Spark.setScriptData("selectedEvent", selectedEvent);
    Spark.setScriptData("CarRestrictionsAsString", getCarRestrictionsAsString(selectedEvent));
    Spark.setScriptData("ManufacturerRestrictionsAsString", getManufacturerRestrictionsAsString(selectedEvent));
    Spark.setScriptData("ClassRestrictionS", selectedEvent.Restrictions.Class.indexOf("S") > -1);
    Spark.setScriptData("ClassRestrictionA", selectedEvent.Restrictions.Class.indexOf("A") > -1);
    Spark.setScriptData("ClassRestrictionB", selectedEvent.Restrictions.Class.indexOf("B") > -1);
    Spark.setScriptData("ClassRestrictionC", selectedEvent.Restrictions.Class.indexOf("C") > -1);

    Spark.setScriptData("TopPrizeBandCount", selectedEvent.TopPrizes.length);
    Spark.setScriptData("BottomPrizeBandCount", selectedEvent.BottomPrizes.length);

    // Gotta convert Epoch back to readable dates...
    // GameSparks works with Epoch times in milliseconds just because it HAS to be friggin' awkward!
    var ReadableStartDate = new Date(selectedEvent.StartDate * 1000);
    var ReadableEndDate = new Date(selectedEvent.EndDate * 1000);
    // And if it wasn't being awkward enough, toIsoString converts the date into ISO-8601, but adds
    // SECONDS. datetime-local fields don't recognise seconds! ARGHHHH
    Spark.setScriptData("StartDate", ReadableStartDate.toISOString().substr(0, 16));
    Spark.setScriptData("EndDate", ReadableEndDate.toISOString().substr(0, 16));

    // Our league duration is stored in minutes. We've gotta convert them to days, hours and minutes.
    var PreMinutesRemaining = selectedEvent.LeagueDuration;
    var MinutesRemaining = selectedEvent.LeagueDuration;
    var DaysRemaining = Math.floor((selectedEvent.LeagueDuration / 1440));
    MinutesRemaining -= DaysRemaining * 1440;
    var HoursRemaining = Math.floor(MinutesRemaining / 60);
    MinutesRemaining -= HoursRemaining * 60;

    Spark.setScriptData("DaysRemaining", DaysRemaining);
    Spark.setScriptData("HoursRemaining", HoursRemaining);
    Spark.setScriptData("MinutesRemaining", MinutesRemaining);
    Spark.setScriptData("PreMinutesRemaining", MinutesRemaining);
}

function getCarRestrictionsAsString(selectedEvent)
{
    return selectedEvent.Restrictions.Car.join(", ");
}

function getManufacturerRestrictionsAsString(selectedEvent)
{
    return selectedEvent.Restrictions.Manufacturer.join(", ");
}

leaderboards_drop.script:
Spark.setScriptData("leaderboard", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};

    var shortCode = data.shortCode;
    var entryId = data.entryId;
    var leaderboard = Spark.getLeaderboards().getLeaderboard(shortCode);

    switch(data.action){
        case "view":
            return view(data);
        case "delete":
            return deletion(data);
    }

    function view(data){

        form.shortCode = shortCode;

        return form;
    }

    // drops the leaderboard and the running total if it's set to true
    function deletion(data){

        var deleteRunningTotal = data.deleteRunningTotal;

        if(deleteRunningTotal != null){
            leaderboard.drop(true);
        }else{
            leaderboard.drop();
        }

        form.updated = true;

        return form;   
    }
}

leaderboards_entry_delete.script:
Spark.setScriptData("entry", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};

    var shortCode = data.shortCode;
    var entryId = data.entryId;
    var leaderboard = Spark.getLeaderboards().getLeaderboard(shortCode);

    switch(data.action){
        case "view":
            return view(data);
        case "delete":
            deletion(data);
            return view(data);
    }

    function view(data){

        var rank = data.rank;

        form.shortCode = shortCode;
        form.entryId = entryId;
        form.rank = rank;

        return form;
    }

    // deletes the entry and the running total if it's set to true
    function deletion(data){

        var deleteRunningTotal = false;

        if(data.deleteRunningTotal){
            deleteRunningTotal = true;
        }

        leaderboard.deleteEntry(entryId, deleteRunningTotal);
        form.updated = true;

        return form;   
    }
}

leaderboards_filter.script:
Spark.setScriptData("form", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};

    switch(data.action){
        case "view":
            return view(data);
        case "delete":
            deletion(data);
            return view(data);
    }

    function view(data){

        var shortCode = data.shortCode;
        var rank = data.rank;
        var count = data.count;

        //if filter didn't receive rank and count parameters, we set them to defaults
        if(rank == null || count == null){
            rank = 1;
            count = 50;
        }

        form.rank = rank;
        form.count = count;
        form.shortCode = shortCode;

        return form;
    }

    function deletion(data){

        // deletes an entry based on Id
        var entryId = data.entryId;
        var deleteRunningTotal = data.deleteRunningTotal;
        var leaderboard = Spark.getLeaderboards().getLeaderboard(shortCode);

        // if deleteRunning totan is set, we delete the entry, and the running total for that entry
        if(deleteRunningTotal != null){
            leaderboard.deleteEntry(entryId, true);
            // otherwise only delete the entry
        }else{
            leaderboard.deleteEntry(entryId, false);
        }
    }
}

leaderboards_main_menu.script:
Spark.setScriptData("data", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};

    return view(data);

    function view(data){

        //get all leaderboards
        var leaderboards = Spark.getLeaderboards().listLeaderboards();

        var aggregatedLeaderboards = [];
        var leaderboard = null;

        // for each leaderboard
        for(var i = 0; i<leaderboards.length; i++){
            leaderboard = leaderboards[i];

            // check if the leaderboard is partitioned
            if(leaderboard.isPartitioned()){
                aggregatedLeaderboards.push({shortCode : leaderboard.getShortCode(), name : leaderboard.getName(), partitioned : true});
            } else{
                aggregatedLeaderboards.push({shortCode : leaderboard.getShortCode(), name : leaderboard.getName()})
            }
        }

        form.leaderboards = aggregatedLeaderboards;

        return form;
    }
}

leaderboards_partition_menu.script:
Spark.setScriptData("partitioned", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};

    return view(data);

    function view(data){

        var shortCode = data.shortCode;
        var leaderboard = Spark.getLeaderboards().getLeaderboard(shortCode);
        var name = leaderboard.getName();
        var partitions = leaderboard.getPartitions();

        // check how many partitions exist
        if(partitions.length > 0){

            var leaderboards = [];
            var partition = null;

            // each partition will be used as an option in a select
            for(var i in partitions){
                partition = partitions[i];
                leaderboards.push({shortCode : partition.getShortCode()});
            }
            form.leaderboards = leaderboards;
        }

        form.shortCode = shortCode;
        form.name = name;

        return form;
    }
}

leaderboards_results.script:
Spark.setScriptData("data", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};

    return view(data);

    function view(data){

        var shortCode = data.shortCode;
        var rank = data.rank;
        var playerId = data.playerId;
        var count = 50;
        var entryCount = 0;

        var leaderboard = Spark.getLeaderboards().getLeaderboard(shortCode);

        if(leaderboard != null){

            var entryCount = leaderboard.getEntryCount();
            var entries = null;
        // get entries based on playerId
        if(playerId){
            entries = leaderboard.getEntriesFromPlayer(playerId, count);
        } else {
            if(!rank){
                rank = 1;
            }
            entries = leaderboard.getEntries(count, rank-1);
        }

        var leaderboardScoreFields = leaderboard.getScoreFields();
        var scoreFields = [];

        for(var i in leaderboardScoreFields){
            scoreFields.push({scoreField: leaderboardScoreFields[i]});
        }

        // calculate the width , based on the number of score fields
        if(scoreFields.length > 0){
            var width = Math.floor(12/scoreFields.length);
        }

        // work out the name and id of the user/team
        var identifierName_attr = null;
        var identifierId_attr = null;

        if(entries.hasNext()){

            var leaderboardEntries = [];

            while(entries.hasNext()){
                var entry = entries.next();

                if(identifierId_attr == null){
                    rank = entry.getRank();
                    if(entry.getAttribute("teamId")){
                        identifierId_attr = "teamId";
                        identifierName_attr = "teamName"
                    } else {
                        identifierId_attr = "userId";
                        identifierName_attr = "userName"
                    }
                }

                leaderboardEntries.push({entry: entry});
            }

            form.entries = leaderboardEntries;
        }

        form.identifierId_attr = identifierId_attr;
        form.identifierName_attr = identifierName_attr;

        form.rank = rank;
        form.count = count;
        form.scoreFields = scoreFields;
        form.width = width;
        form.hiddenId = "hidden_"+shortCode;
        form.shortCode = shortCode;

        }else{
            form.partition = true;
        }

        form.entryCount = entryCount;

        return form;
    }
}

