player_abtests.script:
require("PlayerDataUtilities");

Spark.setScriptData("info", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};
    // load player for view/edit
    var playerId = data.playerId;
    var player = Spark.loadPlayer(playerId);

    form.playerId = playerId;

    switch (data.action) {
        case "view":
            return view(data, player);
        case "submit":
            return submit(data, player);
    }

    function view(data, player) {
        var cursor = Spark.metaCollection("ABTests").find(
        {
            // All entries
        }, 
        {
            // Return
            Name: true,
            Cohorts: true
        });

        var abTests = ["None"];
        while (cursor.hasNext()) {
            var entry = cursor.next();
            for (var cohort in entry.Cohorts) {
                abTests.push(FormatString("{0} : {1}", entry.Name, cohort));
            }
        }
        form.ABTests = abTests;

        abTestName = player.getSegmentValue("ABTest");
        cohort = player.getSegmentValue("Cohort");
        if (abTestName === null || abTestName === undefined || 
            cohort === null || cohort === undefined) {

            form.CurrentPlayerABTest = "None";
        }
        else {
            form.CurrentPlayerABTest = FormatString("{0} : {1}", abTestName, cohort);
        }

        return form;
    }

    function submit(data, player) {
        var testAndCohortStr = data.abTest;
        if (testAndCohortStr === "None") {
            player.setSegmentValue("ABTest", null);
            player.setSegmentValue("Cohort", null);
        }
        else {
            var split = testAndCohortStr.split(":");
            if (split.length !== 2) {
                return form;
            }
            var abTestName = split[0].trim();
            var cohort = split[1].trim();

            player.setSegmentValue("ABTest", abTestName);
            player.setSegmentValue("Cohort", cohort);
        }

        form.submitSuccess = true;

        return view(data, player);
    }
}

player_achievements.script:
Spark.setScriptData("form", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};
    // load player for view/edit
    var playerId = data.playerId;
    var player = Spark.loadPlayer(playerId);

    switch(data.action){
        case "view":
            return view(data);
        case "update":
            update(data);
            return view(data);
    }

    function view(data){

        //get form data for view
        form.playerId = playerId;
        // list achievements for the player
        var listAchievementsRequest = new SparkRequests.ListAchievementsRequest();

        var response = Spark.sendRequestAs(listAchievementsRequest, playerId);
        var earnedAchievements = [];
        var unearnedAchievements = [];

        for(var i in response.achievements){

            var achievement = response.achievements[i];

            // if the player has this achievement add it to the earnedAchievements list
            if(achievement.earned == true){
                earnedAchievements.push({name: achievement.name, shortCode: achievement.shortCode});
                // else add it to the unearnedAchievements list
            } else{
                unearnedAchievements.push({name: achievement.name, shortCode: achievement.shortCode});
            }
        }

        // add the achievements to the form
        form.earnedAchievements = earnedAchievements;
        form.unearnedAchievements = unearnedAchievements;

        return form;
    }

    function update(data){

        // get the achievement to add or remove
        var addAchievement = data.unearnedAchievements;
        var removeAchievement =data.deleteShortCode;

        // add the achievement
        if(addAchievement != null){
            player.addAchievement(addAchievement);
        }

        // remove the achievement
        if(removeAchievement != null){
            player.removeAchievement(removeAchievement);
        }

        // flag the form as updated
        form.updated = true;
    }
}

