player_currencies.script:
Spark.setScriptData("currencies", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};
    // load player for view/edit
    var playerId = data.playerId;
    var player = Spark.loadPlayer(playerId);

    switch(data.action){
        case "view":
            return view(data);
        case "update":
            update(data);
            return view(data);
    }

    function view(data){

        //get form data for view
        form.playerId = playerId;

        // get balance for each currency
        form.currency1 = player.getBalance1();
        form.currency2 = player.getBalance2();
        form.currency3 = player.getBalance3();
        form.currency4 = player.getBalance4();
        form.currency5 = player.getBalance5();
        form.currency6 = player.getBalance6();

        return form;
    }

    function update(data){

        // credit or debit ( if getDifference is negative ) the player
        player.credit1(getDifference(player.getBalance1(), data.currency1));
        player.credit2(getDifference(player.getBalance2(), data.currency2));
        player.credit3(getDifference(player.getBalance3(), data.currency3));
        player.credit4(getDifference(player.getBalance4(), data.currency4));
        player.credit5(getDifference(player.getBalance5(), data.currency5));
        player.credit6(getDifference(player.getBalance6(), data.currency6));

        form.updated = true;   
    }

    // gets the difference between current balance and the one to set
    function getDifference(balance, toSet){

        var toSetInt = parseInt(toSet);
        return toSetInt - balance;
    }
}

player_currenciesLevels.script:
Spark.setScriptData("inventory", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data) {

    var form = {};
    // load player for view/edit
    var playerId = data.playerId;
    var player = Spark.loadPlayer(playerId);

    switch (data.action) {
        case "updateAll":
            updateAll(data);
            return view(data);
        default:
            return view(data);
    }

    function view(data) {

        //get form data for view
        form.playerId = playerId;

        // get balance for each currency
        form.currency1 = player.getBalance1();
        form.currency2 = player.getBalance2();
        //form.currency3 = player.getBalance3();
        //form.currency4 = player.getBalance4();
        //form.currency5 = player.getBalance5();
        form.currency6 = player.getBalance6();

        form.level = player.hasVGood("Level");

        return form;
    }

    function updateAll(data) {

        try {
            // credit or debit ( if getDifference is negative ) the player
            var cashDiff = data.currency1 - player.getBalance1();
            if (cashDiff > 0) {
                player.credit1(cashDiff);
            }
            else {
                player.debit1(Math.abs(cashDiff));
            }

            var goldDiff = data.currency2 - player.getBalance2();
            if (goldDiff > 0) {
                player.credit2(goldDiff);
            }
            else {
                player.debit2(Math.abs(goldDiff));
            }

            //player.credit3(getDifference(player.getBalance3(), data.currency3));
            //player.credit4(getDifference(player.getBalance4(), data.currency4));
            //player.credit5(getDifference(player.getBalance5(), data.currency5));

            var xpDiff = data.currency6 - player.getBalance6();
            if (xpDiff > 0) {
                player.credit6(xpDiff);
            }
            else {
                player.debit6(Math.abs(xpDiff));
            }

            var currentLevel = player.hasVGood("Level");
            var newLevel = data.level;
            if (currentLevel < newLevel) {
                var diff = newLevel - currentLevel;
                player.addVGood("Level", diff);
            }
            else if (currentLevel > newLevel) {
                var diff = currentLevel - newLevel;
                player.useVGood("Level", diff);
            }

            form.updated = true;   
        }
        catch (errorMessage) {
            form.error = true;
            form.errorMessage = errorMessage;
        }
    }
}

player_details.script:
require("PlayerDataUtilities");

Spark.setScriptData("player", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};
    // load player for view/edit
    var playerId = data.playerId;
    var player = Spark.loadPlayer(playerId);

    switch(data.action){
        case "view":
            return view(data);
        case "update":
            update(data);
            return view(data);
        case "nuke":
            nuke(data);
            return view(data);
    }

    function view(data){

        //get form data for view
        form.playerId = playerId;
        form.userName = player.getUserName();
        form.displayName = player.getDisplayName();
        form.registrationDate = player.getCreationDate().toString();
        form.isOnline = player.isOnline();
        form.lastSeen = player.getLastSeen().toString();
        form.isHidden = player.isHiddenOnLeaderboards();


        // Game Center ID
        if (player.getExternalIds().GC != null) {
            form.GCID = player.getExternalIds().GC;
        }

        // Google Play Games ID
        if (player.getExternalIds().GP != null) {
            form.GPGID = player.getExternalIds().GP;
        }

        // Facebook ID
        if (player.getExternalIds().FB != null) {
            form.FBID = player.getExternalIds().FB;
        }

        return form;
    }

    function update(data){

        //create a ChangeUserDetails request
        var changeUserDetailsRequest = new SparkRequests.ChangeUserDetailsRequest();

        var newUserName = data.userName;
        var newDisplayName = data.displayName;
        var hidden = data.hidden;

        // check that old username doesn't match the new one
        if(player.getUserName() != newUserName){
        changeUserDetailsRequest.userName = newUserName;
        }

        changeUserDetailsRequest.displayName = newDisplayName;

        var response = Spark.sendRequestAs(changeUserDetailsRequest, playerId);

        // if ChangeUserDetails response contained an error
        if(response.error != null){
            form.error = JSON.stringify(response.error);
        } else{
            // set leaderboard visibility for the player
            if(hidden == "on"){
                player.hideOnLeaderboards();
            } else{
                player.showOnLeaderboards();
            }

            form.success = true;
        }

        form.updated = true;   
    }

    function nuke(data){
        // Compare the user ID with what was entered into the nuke box.
        if (data.playerId === data.nukeConfirmation)
        {
            NukePlayerProfile(data.playerId);
            form.success = true;
        }
        else
        {
            form.success = false;
            form.error = "The text in the nuke box doesn't match the user's ID."
        }

        form.nuked = true;
    }
}

player_event_data.script:
require("MathUtilities");
require("GeneralUtilities");

Spark.setScriptData("data", SnippetProcessor(Spark.getData().scriptData));

function SnippetProcessor(data) {
    var playerId = data.playerId;
    var count = Spark.systemCollection("challengeInstance").find(
    {
        "accepted": playerId, "challengeName": { $not: {$eq: "FTUE Opponent Race"} }
    }).count();
    var limit = 10;
    var maxPages = Math.ceil(count / limit);
    var page = Clamp(parseInt(data.page), 1, maxPages); // parseInt, cuz HTML is cool and doesn't need numbers
    var skip = (page - 1) * limit;

    var hbEventData = {};
    var playerProfile = Spark.runtimeCollection("PlayerProfile").findOne(
    {
        "_id": { "$oid": playerId }
    },
    {
        "_id": 0
    });

    function GetTempScoreForChallenge(challengeId, playerId) {
        var tempScores = playerProfile.tempScores;
        for (var i = 0; i < tempScores.length; ++i) {
            for (var key in tempScores[i]) {
                if (key === challengeId) {
                    var score = tempScores[i][key];
                    if (score === null || score === undefined) {
                        return 0;
                    }
                    else if (isNaN(score)) {
                        Spark.getLog.error(FormatString("PlayerDataUtilities.GetTempScoreForChallenge() tempScore for {0} is NaN", key));
                        return 0;
                    }
                    else {
                        return score;
                    }
                }
            }
        }
    }

    function PrizeToShortString(prize) {
        if (prize !== null && prize !== undefined) {
            if (prize.PrizeType === "Car") {
                return prizeString = FormatString("{0}: {1}", prize.PrizeType, prize.PrizeValue);
            }
            else if (prize.PrizeType === "Blueprint") {
                return prizeString = FormatString("{0}x {1}: {2}", prize.BlueprintOrProPackCount, prize.PrizeType, prize.PrizeValue);
            }
            else if (prize.PrizeType === "ProPack") {
                return FormatString("{0}x Class {1} {2}: {3}", prize.BlueprintOrProPackCount, prize.PrizeClass, prize.PrizeType, prize.PrizeValue);
            }
            else {
                return FormatString("{0} {1}", SeparateThousands(prize.PrizeValue), prize.PrizeType);
            }
        }
    }

    // When players win prizes for an event, the event data is deleted from their profile after they
    // collect their prizes. So I know what prizes they won, but not what events they entered.
    // Well... since that data's volatile, let's query challenges instead.
    // First, get the challenges the player accepted.
    var acceptedChallengesCursor = Spark.systemCollection("challengeInstance").find(
        {
            "accepted": playerId, "challengeName": { $not: {$eq: "FTUE Opponent Race"} }
        }).sort({"endDate": -1}).limit(limit).skip(skip);

    // Now we need to go through each challenge. Grab the challenge ID and the challenge state.
    var enteredEvents = {};
    while (acceptedChallengesCursor.hasNext()) {
        var challenge = acceptedChallengesCursor.next();
        if (enteredEvents[challenge.challengeName] == null) {
            enteredEvents[challenge.challengeName] = [];
        }

        var challengeId = challenge._id.$oid;
        var challengeState = challenge.state;
        var totalChallengers = challenge.accepted.length;

        // Now we can grab data from the leaderboard.
        // If the leaderboard state is "ISSUED" then the player is the only one in the leaderboard, and
        // the leaderboard won't exist yet.
        var playerRank = "N/A";
        var score;
        if (challengeState === "ISSUED" || challengeState === "EXPIRED") {
            // This player's the only one in the leaderboard!
            playerRank = 1;

            // Of course, we can't get the player's score from the leaderboard if they're the only one in
            // there (cuz the leaderboard doesn't exist, thx GameSpacks), so we need to grab that data from
            // their player profile; it's stored in tempScores.
            // However, if they've claimed their prizes when they were the only person in the leaderboard,
            // we have literally no way of getting their score. Epic.
            score = GetTempScoreForChallenge(challengeId, playerId)
            if (score !== null && score !== undefined) {
                score = "$" + SeparateThousands(score);
            }
            else {
                score = "N/A";
            }
        }
        else {
            var challengeLeaderboard = Spark.getLeaderboards().getChallengeLeaderboard(challengeId);
            if (challengeLeaderboard === null || challengeLeaderboard === undefined) {
                // Wat? The challenge leaderboard doesn't exist? Uh... continue, I guess!
                continue;
            }
            var playerEntries = challengeLeaderboard.getEntriesFromPlayer(playerId, 1);
            if (playerEntries.hasNext()) {
                var playerLeaderboardEntry = playerEntries.next();
                playerRank = playerLeaderboardEntry.getRank();
                score = "$" + SeparateThousands(playerLeaderboardEntry.getAttribute("score"));
            }
        }

        // If the challenge is finished and the player has a valid rank, we can use that along
        // with the event name to figure out what prizes they *should* have earned.
        var prizes = [];
        if (challengeState === "EXPIRED" || challengeState === "COMPLETE") {
            if (playerRank !== "N/A") {
                var eventPrizeData = Spark.metaCollection("raceEvents").findOne(
                {
                    "EventName": challenge.challengeName
                }, 
                {
                    "TopPrizes": 1, "_id": 0
                }).TopPrizes;

                for (var iPrizeBands = 0; iPrizeBands < eventPrizeData.length; ++iPrizeBands) {
                    var prizeBand = eventPrizeData[iPrizeBands];

                    // Grab the first band that we beat the rank of (if any).
                    if (playerRank <= parseInt(prizeBand.GoalTarget)) {
                        for (var iPrizes = 0; iPrizes < prizeBand.Prizes.length; ++iPrizes) {
                            var prize = prizeBand.Prizes[iPrizes];
                            prizes.push({"prize": PrizeToShortString(prize), "state": "Unawarded"});
                        }
                        break;
                    }
                }
            }
            else {
                prizes.push({"prize": "N/A", "state": "Unawarded"});
            }
        }
        // If the challenge is in progress, we can use their current position to determine
        // the prizes they'll win if they hold that position. (do we need to?)
        else if (challengeState === "ISSUED" || challengeState === "RUNNING") {
            //prizes.push({"prize": "Challenge In Progress", "state": "Unclaimed"});
        }

        // Okay, now we have a list of prizes the player *should* have. Now we need to check
        // what prizes the player *actually* has.
        // The unclaimed prize list is exclusively for event goals, so we just need to check the claimed
        // prize list.
        var claimedPrizes = Spark.runtimeCollection("PlayerProfilePrizes").findOne(
        {
            "_id": {"$oid": playerId}
        }, 
        {
            "_id": 0, "eventPrizes.archived": 1
        }).eventPrizes.archived;

        for (var iExpectedPrizes = 0; iExpectedPrizes < prizes.length; ++iExpectedPrizes) {
            var expectedPrize = prizes[iExpectedPrizes];

            for (var iClaimedPrizes = 0; iClaimedPrizes < claimedPrizes.length; ++iClaimedPrizes) {
                var claimedPrize = claimedPrizes[iClaimedPrizes];

                // If this prize is a prize for this event, then we should see if any prize in the prize band
                // matches our current expected prize. If it does, we set the state of the expected prize to
                // "Awarded".
                if (claimedPrize.AwardType === "TopDown" && claimedPrize.ChallengeId === challengeId) {
                    for (var iClaimedPrizeBandPrize = 0; iClaimedPrizeBandPrize < claimedPrize.PrizeBand.Prizes.length; ++iClaimedPrizeBandPrize) {
                        var claimedPrizeBandPrize = claimedPrize.PrizeBand.Prizes[iClaimedPrizeBandPrize];

                        if (expectedPrize.prize === PrizeToShortString(claimedPrizeBandPrize)) {
                            expectedPrize.state = "Awarded";
                            break;
                        }
                    }
                }
            }
        }

        // Add all the data we need for this challenge, and we're sorted; onto the next challenge.
        enteredEvents[challenge.challengeName].push(
        {
            "challengeId": challengeId,
            "endDate": challenge.endDate,
            "state": challengeState,
            "rank": playerRank,
            "totalChallengers": totalChallengers,
            "score": score,
            "prizes": prizes
        });
    }

    // Finally, put the data together for Handlebars!
    hbEventData.events = enteredEvents;
    hbEventData.queryInfo = {
        "playerId": data.playerId,
        "page": page,
        "maxPages": maxPages,
        "startRange": skip + 1,
        "endRange": Clamp(limit + skip, 0, acceptedChallengesCursor.count()),
        "count": count
    };
    return hbEventData;
}

player_iap_log.script:
require("GeneralUtilities");

Spark.setScriptData("log", SnippetProcessor(Spark.getData().scriptData));

function SnippetProcessor(data) {
    var playerId = data.playerId;

    // Undocumented method HOOOOOOOOO!
    var verifiedPurchaseCollection = Spark.systemCollection("verifiedPurchase").find(
    { 
        playerId: playerId 
    }
    ).sort(
    {
        when: -1
    });

    var transactionAuditCollection = Spark.systemCollection("playerTransactionAudit");
    var iapLog = [];

    // We need to match up data from verified purchases with data from the transaction audit.
    // When GameSpacks is nice enough to respond to my message I sent about two months ago about how their currencyCode
    // and subUnitPrice data is fucked* then we can actually display how much they spent on stuff.
    // *not what I actually said
    while (verifiedPurchaseCollection.hasNext()) {
        var logEntry = {
            transactionId: "",
            productShortCode: "",
            date: "",
            verified: false,
            currencyCredited: 0,
        };

        var transaction = verifiedPurchaseCollection.next();
        logEntry.transactionId = transaction.transactionId;
        logEntry.productShortCode = transaction.productShortCode;
        logEntry.date = UnixTimeToReadableString(transaction.when);

        var auditEntries = transactionAuditCollection.find(
        { 
            playerId: playerId, 
            transactionId: {
                "$exists": true
            },
            transactionId: transaction.transactionId 
        });

        while (auditEntries.hasNext()) {
            var auditEntry = auditEntries.next();

            var auditCurrency = auditEntry.currency;

            if (auditCurrency !== null && auditCurrency !== undefined) {
                var auditCurrency1 = auditCurrency.currency1;
                var auditCurrency2 = auditCurrency.currency2;

                if (auditCurrency1 !== null && auditCurrency1 !== undefined) {
                    logEntry.currencyCredited = auditCurrency1.amount;
                    logEntry.currencyType = "Cash";
                }
                else if (auditCurrency2 !== null && auditCurrency2 !== undefined) {
                    logEntry.currencyCredited = auditCurrency2.amount;
                    logEntry.currencyType = "Gold";
                }
            }

            logEntry.verified = true;
        }

        iapLog.push(logEntry);
    }

    return iapLog;
}

