player_prize_awarder.script:
require("PlayerDataUtilities");
require("RaceEventUtilities");

Spark.setScriptData("info", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};
    // load player for view/edit
    var playerId = data.playerId;
    var player = Spark.loadPlayer(playerId);

    form.playerId = playerId;

    switch (data.action) {
        case "view":
            return view(data);
        case "awardPrize":
            return awardPrize(data, playerId);
    }

    function view(data) {
        // default values
        form.eventName = "Event Name";
        form.rank = 1;
        form.cash = 0;
        form.gold = 0;
        form.bronzeKeys = 0;
        form.silverKeys = 0;
        form.goldKeys = 0;
        form.XP = 0;
        form.car = "Car Variant ID";
        form.proPackQuantity = 0;
        form.blueprintCar = "Car Variant ID";
        form.blueprintQuantity = 0;

        // Let's make this easier for CS.
        AddPossibleEventNamesToForm();

        // Populate manufacturers and classes lists
        var allManufacturers = Spark.metaCollection("CarModels").distinct("ClientData.Manufacturer");
        var allClasses = Spark.metaCollection("CarModels").distinct("ClientData.Class");
        var allBlueprintCars = GetAllBlueprintCars();

        allManufacturers.sort();
        allClasses.sort();

        form.manufacturers = allManufacturers;
        form.classes = allClasses;
        form.blueprintCars = allBlueprintCars;

        return form;
    }

    function awardPrize(data, playerId) {
        // Re-add all the values we just had
        form.eventName = data.eventName;
        form.rank = data.rank;
        form.cash = data.cash;
        form.gold = data.gold;
        form.bronzeKeys = data.bronzeKeys;
        form.silverKeys = data.silverKeys;
        form.goldKeys = data.goldKeys;
        form.XP = data.XP;
        form.car = data.car;
        form.proPackQuantity = data.proPackQuantity;
        form.blueprintCar = data.blueprintCar;
        form.blueprintQuantity = data.blueprintQuantity;

        // Let's make this easier for CS.
        AddPossibleEventNamesToForm();

        var allManufacturers = Spark.metaCollection("CarModels").distinct("ClientData.Manufacturer");
        var allClasses = Spark.metaCollection("CarModels").distinct("ClientData.Class");
        var allBlueprintCars = GetAllBlueprintCars();

        allManufacturers.sort();
        allClasses.sort();

        form.manufacturers = allManufacturers;
        form.classes = allClasses;
        form.blueprintCars = allBlueprintCars;
        // This doesn't appear to work, not sure why :(
        form.selectedPPManufacturer = data.ppManufacturers;
        form.selectedPPClass = data.ppClasses;
        form.selectedBlueprintCar = data.blueprintCar;

        var eventName = form.eventName;
        var localisedTextTag = "";
        var event = Spark.metaCollection("raceEvents").findOne({"EventName": eventName}, 
                                        {"_id": 1, "LocalisedTextTag": 1});
        if (event === null || event === undefined) {
            form.eventError = true;
            form.message = "\"" + eventName + "\" is not a valid event";
            return form;
        }
        else {
            localisedTextTag = event.LocalisedTextTag;
        }

        var carVariantId = form.car;
        if (carVariantId !== "" && carVariantId !== "Car Variant ID") {
            var car = Spark.metaCollection("CarInventory").findOne({"CarVariantID": carVariantId}, {"_id": 1});
            if (car === null || car === undefined) {
                form.carError = true;
                form.message = "Not a valid car variant ID";
                return form;
            }
        }

        if (data.blueprintCar !== null && data.blueprintCar !== undefined && data.blueprintCar !== "" && 
            data.blueprintCar !== "None") {

            if (form.blueprintQuantity < 1) {
                form.blueprintCarError = true;
                form.message = "Quantity needed";
                return form;
            }
        }

        // Try and get a valid challenge id for a challenge instance we have been in and has finished
        var challenge = Spark.systemCollection("challengeInstance").findOne(
            {
                challengeName: form.eventName,
                accepted: {
                    "$in": [playerId]
                },
                state: {
                    "$in": GetFinishedRunStatesArray()
                }
            },
            {
                _id: true
            }
        );

        if (challenge === null || challenge === undefined) {
            // Couldn't find a challenge for the player that matched all the criteria,
            // so we'll just take a challenge with the same event name that has finished
            challenge = Spark.systemCollection("challengeInstance").findOne(
                {
                    challengeName: form.eventName,
                    state: {
                        "$in": GetFinishedRunStatesArray()
                    }
                },
                {
                    _id: true
                }
            );
        }

        var unawardedPrize = {};
        unawardedPrize.AwardType = "TopDown";
        unawardedPrize.EventName = eventName;
        unawardedPrize.LocalisedTextTag = localisedTextTag;
        unawardedPrize.PlayerRank = parseInt(form.rank);
        unawardedPrize.Time = Math.floor(Date.now() / 1000);

        if (challenge !== null && challenge !== undefined) {
            unawardedPrize.ChallengeId = challenge._id.$oid;
        }

        var prizeBand = {};
        prizeBand.Goal = "FinishAtRank";
        prizeBand.GoalTarget = form.rank;

        var prizes = [];
        // Cash
        if (data.cash > 0 && data.cash !== null && data.cash !== undefined && !isNaN(data.cash)) {
            var prize = {};
            prize.PrizeType = "Cash";
            prize.PrizeValue = data.cash;
            prize.PrizeClass = null;
            prize.BlueprintOrProPackCount = 0;
            prizes.push(prize);
        }
        // Gold
        if (data.gold > 0 && data.gold !== null && data.gold !== undefined && !isNaN(data.gold)) {
            var prize = {};
            prize.PrizeType = "Gold";
            prize.PrizeValue = data.gold;
            prize.PrizeClass = null;
            prize.BlueprintOrProPackCount = 0;
            prizes.push(prize);
        }
        // XP
        if (data.XP > 0 && data.XP !== null && data.XP !== undefined && !isNaN(data.XP)) {
            var prize = {};
            prize.PrizeType = "XP";
            prize.PrizeValue = data.XP;
            prize.PrizeClass = null;
            prize.BlueprintOrProPackCount = 0;
            prizes.push(prize);
        }
        // Bronze Keys
        if (data.bronzeKeys > 0 && data.bronzeKeys !== null && data.bronzeKeys !== undefined && 
            !isNaN(data.bronzeKeys)) {

            var prize = {};
            prize.PrizeType = "BronzeKeys";
            prize.PrizeValue = data.bronzeKeys;
            prize.PrizeClass = null;
            prize.BlueprintOrProPackCount = 0;
            prizes.push(prize);
        }
        // Silver Keys
        if (data.silverKeys > 0 && data.silverKeys !== null && data.silverKeys !== undefined && 
            !isNaN(data.silverKeys)) {

            var prize = {};
            prize.PrizeType = "SilverKeys";
            prize.PrizeValue = data.silverKeys;
            prize.PrizeClass = null;
            prize.BlueprintOrProPackCount = 0;
            prizes.push(prize);
        }
        // Gold Keys
        if (data.goldKeys > 0 && data.goldKeys !== null && data.goldKeys !== undefined && !isNaN(data.goldKeys)) {
            var prize = {};
            prize.PrizeType = "GoldKeys";
            prize.PrizeValue = data.goldKeys;
            prize.PrizeClass = null;
            prize.BlueprintOrProPackCount = 0;
            prizes.push(prize);
        }
        // Cars
        if (data.car !== "" && data.car !== "Car Variant ID") {
            var prize = {};
            prize.PrizeType = "Car";
            prize.PrizeValue = data.car;
            prize.PrizeClass = null;
            prize.BlueprintOrProPackCount = 0;
            prizes.push(prize);
        }
        // Pro Packs
        if (data.proPackQuantity > 0 && data.proPackQuantity !== null && data.proPackQuantity !== undefined && 
            !isNaN(data.proPackQuantity)) {

            var prize = {};
            prize.PrizeType = "ProPack";
            prize.PrizeValue = data.ppManufacturers;
            prize.PrizeClass = data.ppClasses;
            prize.BlueprintOrProPackCount = data.proPackQuantity;
            prizes.push(prize);
        }
        // Blueprints
        if (data.blueprintQuantity > 0 && data.blueprintQuantity !== null && data.blueprintQuantity !== undefined && 
            !isNaN(data.blueprintQuantity) && 
            data.blueprintCar !== "" && data.blueprintCar !== "Car Variant ID" &&
            data.blueprintCar !== null && data.blueprintCar !== undefined) {

            var prize = {};
            prize.PrizeType = "Blueprint";
            prize.PrizeValue = data.blueprintCar;
            prize.PrizeClass = null;
            prize.BlueprintOrProPackCount = parseInt(data.blueprintQuantity);
            prizes.push(prize);
        }

        if (prizes.length === 0) {
            return form;
        }

        prizeBand.Prizes = prizes;
        unawardedPrize.PrizeBand = prizeBand;

        var versionedPrizes = GetVersionedEventPrizes(playerId);
        var successfullyWritten = false;

        while (!successfullyWritten) {
            var eventPrizes = versionedPrizes.GetData();
            eventPrizes.unawarded.push(unawardedPrize);
            successfullyWritten = versionedPrizes.SetData(eventPrizes);
        }

        form.prizeAwarded = true;

        return form;
    }

    function GetAllBlueprintCars() {
        var cursor = Spark.metaCollection("CarInventory").find(
            {
                BlueprintPiecesRequired: {
                    $gt: 0
                }
            },
            {
                CarVariantID: true   
            }
        );
        var allBlueprintCars = [];
        if (cursor !== null && cursor !== undefined) {
            while (cursor.hasNext()) {
                var blueprintCar = cursor.next();
                allBlueprintCars.push(blueprintCar.CarVariantID);
            }
        }
        allBlueprintCars.sort();
        allBlueprintCars.splice(0, 0, "None");

        return allBlueprintCars;
    }

    function AddPossibleEventNamesToForm()
    {
        var possibleEventNames = Spark.metaCollection("raceEvents").aggregate(
            { $match: { "LocalisedTextTag": { $regex: "TEXT_" } } },
            { $group: { _id: null, "EventNames": { "$push" : "$EventName"} } });
        form.EventNames = possibleEventNames[0].EventNames;
        form.EventNames.sort();
    }
}

