Spark.setScriptData("form", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){
    var form = {};
    if(data.action === "update"){
        return update(form, data);
    }
    return view(form, data);

    function update(form, data){
        if(data.level){
            Spark.getLog().setLevel(data.level);
            form.updated = true;
        }
        return view(form, data);
    }

    function view(form, data){
        form.levels = ["ERROR", "WARN", "INFO", "DEBUG"];
        form.current = Spark.getLog().getLevel();
        return form;
    }
}

script_log_search_results.script:

Spark.setScriptData("results", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var query = data.query;
    var logs = Spark.runtimeCollection("log");
    var limit = 50;

    var entries = logs.find(query).limit(limit).sort({ts : -1});

    var count = entries.count();
    if(count < limit){
        limit = count;
    }

    return {
        query : query,
        count : count,
        limit : limit,
        entries : entries
    };
}

serverClockMainPage.script:
requireOnce("TimeUtilities");
requireOnce("GeneralUtilities");

Spark.setScriptData("data", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    data.submitted = false;
    data.reset = false;
    var now = Date.now();

    switch (data.action) {
        case "enter":
            // Blank
            break;
        case "submit":
            if (data.epochNow === "") {
                // Set offset
                data = SetServerClockOffset(data);
            }
            else {
                // Set 'now'
                data = SetServerClockNow(now, data);
            }
            break;
    }

    data.now = now;
    data.serverClockNow = GetNow();
    data.nowISO = new Date(now).toISOString();
    data.serverClockNowISO = new Date(GetNow()).toISOString();
    var diffDate = new Date(GetNow() - now);
    data.difference = CalculateDifference(GetNow(), now);

    function SetServerClockNow(now, data) {
        var serverClockNow = data.epochNow;

        if (serverClockNow < 0 || serverClockNow === null || serverClockNow === undefined || 
            serverClockNow === "" || isNaN(serverClockNow)) {

            SetServerTimeOffset(0);
            data.reset = true;
            Spark.getLog().info(FormatString(
                "SERVER CLOCK: Reset server clock fudge to 0, now is {0} [{1}]", 
                now,
                new Date(now).toISOString()));
        }
        else {
            var str = serverClockNow.toString();
            //Spark.getLog().info(FormatString("submitted as str {0}", str));
            while(str.length < 13) {
                str += "0";
            }
            //Spark.getLog().info(FormatString("padded str {0}", str));
            serverClockNow = parseInt(str);
            //Spark.getLog().info(FormatString("padded num {0}", serverClockNow));

            SetNow(serverClockNow);
            data.submitted = true;
            Spark.getLog().info(FormatString(
                "SERVER CLOCK: Actual now {0} [{1}], new now {2} [{3}], diff {4}ms", 
                now,
                new Date(now).toISOString(),
                GetNow(),
                new Date(GetNow()).toISOString(),
                GetNow() - now));
        }

        return data;
    }

    function SetServerClockOffset(data) {
        var offsetDays = data.offsetDays;
        var offsetHours = data.offsetHours;
        var offsetMinutes = data.offsetMinutes;
        var offsetSeconds = data.offsetSeconds;

        //Spark.getLog().info(FormatString("offsetDays {0} offsetHours {1} offsetMinutes {2} offsetSeconds {3}", 
        //    offsetDays, offsetHours, offsetMinutes, offsetSeconds));

        var totalOffset = 0;
        totalOffset += (IsValid(offsetSeconds)  ? offsetSeconds : 0)    * 1000;
        totalOffset += (IsValid(offsetMinutes)  ? offsetMinutes : 0)    * 1000 * 60;
        totalOffset += (IsValid(offsetHours)    ? offsetHours   : 0)    * 1000 * 60 * 60;
        totalOffset += (IsValid(offsetDays)     ? offsetDays    : 0)    * 1000 * 60 * 60 * 24;

        var currentServerClock = GetNow();
        SetNow(currentServerClock + totalOffset);

        data.submitted = true;

        return data;
    }

    function IsValid(number) {
        return (number !== null && number !== undefined && number !== "" && !isNaN(number));
    }

    function CalculateDifference(serverClock, unix) {
        var diffMs = Math.abs(serverClock - unix);
        var behind = (serverClock < unix);
        var diffDays = Math.floor(diffMs / 1000 / 60 / 60 / 24).toFixed(0);
        var diffHours = Math.floor(diffMs / 1000 / 60 / 60).toFixed(0) % 24;
        var diffMinutes = Math.floor(diffMs / 1000 / 60).toFixed(0) % 60;
        var diffSeconds = Math.floor(diffMs / 1000).toFixed(0) % 60;
        return FormatString("{0} Day{1}   {2} Hour{3}   {4} Minute{5}   {6} Second{7} {8}",
                            diffDays, 
                            (diffDays > 1) ? "s" : "",
                            diffHours, 
                            (diffHours > 1) ? "s" : "",
                            diffMinutes, 
                            (diffMinutes > 1) ? "s" : "",
                            diffSeconds,
                            (diffSeconds > 1) ? "s" : "",
                            (behind) ? "Behind" : "Ahead");
    }

    return data;
}

showTopDownPrizeBands.script:
var eventId = Spark.data.scriptData.eventId;

if (eventId != null)
{
    var selectedEvent = Spark.runtimeCollection("raceEvents").findOne({"_id" : {$oid : eventId}});
    Spark.setScriptData("EventId", eventId);
    Spark.setScriptData("SelectedEventPrizes", selectedEvent.TopPrizes);

    if (Spark.data.scriptData.addNew != null)
    {
        // Get additional prize bands that are in the form but not in the runtime collection.
        var additionalTargets = [];
        var numberOfAdditionalPrizes = 0;
        var looping = true;

        while (looping)
        {
            var target = (selectedEvent.TopPrizes.length - 1 + numberOfAdditionalPrizes);
            var newPrize = { Target: eval("Spark.data.scriptData.BottomTarget" + target.toString()),
                             TargetPrizeType: eval("Spark.data.scriptData.BottomTargetPrize" + target.toString()),
                             TargetPrizeValue: eval("Spark.data.scriptData.BottomTargetPrizeValue" + target.toString()) };

            if (newPrize.Target == undefined ||
                newPrize.TargetPrizeType == undefined ||
                newPrize.TargetPrizeValue == undefined)
            {
                looping = false;
            }
            else
            {
                additionalTargets.push(newPrize);
                numberOfAdditionalPrizes++;
            }
        }

        // Pass them into HTML so they can be rendered.
        Spark.setScriptData("AdditionalEventPrizes", additionalTargets);

        // And add the new one.
        Spark.setScriptData("AdditionalStartIndex", selectedEvent.TopPrizes.length);
        Spark.setScriptData("NewItemIndex", (numberOfAdditionalPrizes + selectedEvent.TopPrizes.length));
        Spark.setScriptData("AddNew", Spark.data.scriptData.addNew);
    }
}

updateRaceEvent.script:
var queriedEventId = Spark.data.scriptData.eventId;
var eventCollection = Spark.runtimeCollection("raceEvents");
var currentEvent = eventCollection.findOne({"_id": { "$oid": queriedEventId}});

// Checkboxes are either "on" or "undefined".
// AAAAARGH why can't it just be "true" or "false"?!
var VisibleIfIneligible = false;
var DebugEvent = false;
var AlwaysActive = false;
var IsSlam = false;
var EventExpired = false;

if (Spark.data.scriptData.VisibleIfIneligible === "on")
    VisibleIfIneligible = true;
if (Spark.data.scriptData.DebugEvent === "on")
    DebugEvent = true;
if (Spark.data.scriptData.AlwaysActive === "on")
    AlwaysActive = true;
if (Spark.data.scriptData.IsSlam === "on")
    IsSlam = true;
if (Spark.data.scriptData.Expired === "on")
    EventExpired = true;

// Restrictions.
// GameSparks just keeps on sucking. If you give multiple checkboxes the
// same name, submitting the form normally returns all the values you
// selected. GameSparks doesn't. It only returns the first value.
// Hurrrrr...
var ClassRestrictions = [];
if (Spark.data.scriptData.ClassRestrictionS) { ClassRestrictions.push("S") };
if (Spark.data.scriptData.ClassRestrictionA) { ClassRestrictions.push("A") };
if (Spark.data.scriptData.ClassRestrictionB) { ClassRestrictions.push("B") };
if (Spark.data.scriptData.ClassRestrictionC) { ClassRestrictions.push("C") };

var CarRestrictions = [];
if (Spark.data.scriptData.CarRestrictions.trim().length > 0)
    CarRestrictions = Spark.data.scriptData.CarRestrictions.split(", ");

var ManufacturerRestrictions = [];
if (Spark.data.scriptData.ManufacturerRestrictions.trim().length > 0)
    ManufacturerRestrictions = Spark.data.scriptData.ManufacturerRestrictions.split(", ");

var BalanceRestriction = Spark.data.scriptData.BalanceRestriction;
if (BalanceRestriction != null && BalanceRestriction != "")
    BalanceRestriction = parseInt(BalanceRestriction);
else BalanceRestriction = 0;

var Restrictions = { Class: ClassRestrictions,
                     Car: CarRestrictions,
                     Manufacturer: ManufacturerRestrictions,
                     Balance: BalanceRestriction };

// League Duration.
// GameSparks, entertainingly, stores values from a "number" field as strings. You'll need to use
// parseInt() to convert them to doubles. Yes, not ints. Doubles. Be super-careful you don't try to
// parse nulls or anything like that though cuz if you somehow manage to get a NaN in the runtime
// collection it completely fails to retrieve any element of the collection until you remove the entry
// manually. Nope, no error logging or useful error message.
// Such lel.
// It's in minutes, by the way.
var LeagueDurationDays = Spark.data.scriptData.LeagueDurationD;
if (LeagueDurationDays != null && LeagueDurationDays != "")
    LeagueDurationDays = parseInt(LeagueDurationDays);
else
    LeagueDurationDays = 1;

var LeagueDurationHours = Spark.data.scriptData.LeagueDurationH;
if (LeagueDurationHours != null && LeagueDurationHours != "")
    LeagueDurationHours = parseInt(LeagueDurationHours);
else
    LeagueDurationHours = 0;

var LeagueDurationMinutes = Spark.data.scriptData.LeagueDurationM;
if (LeagueDurationMinutes != null && LeagueDurationMinutes != "")
    LeagueDurationMinutes = parseInt(LeagueDurationMinutes);
else
    LeagueDurationMinutes = 0;

var LeagueDurationTotalMinutes = (LeagueDurationDays * 1440) + (LeagueDurationHours * 60) + LeagueDurationMinutes;

// Dates.
// Also, gotta convert and store our dates as Epoch so we can use proper MongoDB queries.
// Arrrggh! Date.parse() doesn't fucking recognise ISO-8601 format! The same format
// used by the friggin' date fields in the form! Oh, and in addition, GameSparks'
// Epoch times are in MILLISECONDS!
// MORE GAMESPARKS WORKAROUND SHIT YAAAAAY!
var StartDateAsEpoch = 0;
var EndDateAsEpoch = (Date.now() / 1000) + 86400;
if (Spark.data.scriptData.StartDate != null)
{
    var RecognizableStartDate = Spark.data.scriptData.StartDate.replace(/-/g, "/").replace("T", " ");
    StartDateAsEpoch = Date.parse(RecognizableStartDate) / 1000;
}
if (Spark.data.scriptData.EndDate != null)
{
    var RecognizableEndDate = Spark.data.scriptData.EndDate.replace(/-/g, "/").replace("T", " ");
    EndDateAsEpoch = Date.parse(RecognizableEndDate) / 1000;
}
if (AlwaysActive)
{
    EndDateAsEpoch = StartDateAsEpoch + (LeagueDurationTotalMinutes * 60);
}

// If we change the event's AlwaysActive state, we need to make sure the event's times are
// changed to prevent the event from ending immediately.
// For example, the default value for event duration is 24 hours. Suddenly making the event
// always active will make the event expire if the start date is at least 24 hours in the past.
if (currentEvent.AlwaysActive != AlwaysActive)
{
    if (AlwaysActive)
    {
        StartDateAsEpoch = (Date.now() / 1000);
    }
    if (!AlwaysActive)
    {
        EndDateAsEpoch = (Date.now() / 1000) + 86400;
    }
}

// Event Prizes.
// Some people say eval() is evil. They can go suck it, because eval() is AWESOME.
var gettingTopTargets = true;
var gettingBottomTargets = true;
var TopTargets = [];
var BottomTargets = [];
var iteration = 0;

while(gettingTopTargets)
{
    var newTopTarget = { Target: eval("Spark.data.scriptData.TopTarget" + iteration.toString()),
                         TargetPrizeType: eval("Spark.data.scriptData.TopTargetPrize" + iteration.toString()),
                         TargetPrizeValue: eval("Spark.data.scriptData.TopTargetPrizeValue" + iteration.toString()) };

    if (newTopTarget.Target == undefined ||
        newTopTarget.TargetPrizeType == undefined ||
        newTopTarget.TargetPrizeValue == undefined)
    {
        Spark.getLog().debug("Top targets done! Iteration " + iteration);
        gettingTopTargets = false;
        iteration = 0;
    }
    else
    {
        // We parse the ints here. We can't parse them immediately after the eval because parsing
        // undefined returns 0. Because it's STUPID.
        newTopTarget.Target = parseInt(newTopTarget.Target);
        newTopTarget.TargetPrizeValue = parseInt(newTopTarget.TargetPrizeValue);

        TopTargets.push(newTopTarget);
        iteration++;
        Spark.getLog().debug("Getting top targets. Iteration " + iteration);
    }
}

while(gettingBottomTargets)
{
    var newBottomTarget = { Target: eval("Spark.data.scriptData.BottomTarget" + iteration.toString()),
                            TargetPrizeType: eval("Spark.data.scriptData.BottomTargetPrize" + iteration.toString()),
                            TargetPrizeValue: eval("Spark.data.scriptData.BottomTargetPrizeValue" + iteration.toString()) };

    if (newBottomTarget.Target == undefined ||
        newBottomTarget.TargetPrizeType == undefined ||
        newBottomTarget.TargetPrizeValue == undefined)
    {
        //Spark.getLog().debug("Bottom targets done! Iteration " + iteration);
        gettingBottomTargets = false;
    }
    else
    {
        // Again, ints are parsed here because (parseInt(undefined) != undefined) == "DUMB".
        newBottomTarget.Target = parseInt(newBottomTarget.Target);
        newBottomTarget.TargetPrizeValue = parseInt(newBottomTarget.TargetPrizeValue);

        BottomTargets.push(newBottomTarget);
        iteration++;
        //Spark.getLog().debug("Getting bottom targets. Iteration " + iteration);
    }
}

var success = eventCollection.update({"_id" : {$oid : queriedEventId}}, { $set:
                                     {
                                        EventName : Spark.data.scriptData.EventName,
                                        EventType : Spark.data.scriptData.EventType,
                                        Track : Spark.data.scriptData.Track,
                                        PreviousWinner : Spark.data.scriptData.PreviousWinner,
                                        VisibleIfIneligible : VisibleIfIneligible,
                                        DebugEvent : DebugEvent,
                                        Restrictions: Restrictions,
                                        AlwaysActive : AlwaysActive,
                                        IsSlam : IsSlam,
                                        StartDate : StartDateAsEpoch,
                                        EndDate : EndDateAsEpoch,
                                        Expired : EventExpired,
                                        LeagueDuration : LeagueDurationTotalMinutes,
                                        AssetBundle: Spark.data.scriptData.AssetBundle,
                                        Sort: parseInt(Spark.data.scriptData.Sort),
                                        PartitionSize: parseInt(Spark.data.scriptData.PartitionSize),
                                        "OverrideEvent" : false,
                                        TopPrizes: TopTargets,
                                        BottomPrizes: BottomTargets
                                     }
                                    });

Spark.setScriptData("id", queriedEventId);
Spark.setScriptData("success", success);

user_query.script:
Spark.setScriptData("config", SnippetProcessor(Spark.getData().scriptData))

function SnippetProcessor(data){

    var form = {};

    return view(data);

    function view(data){

        var config = Spark.getConfig();

        // raw rules allow setting the view of user query
        var rawRules = data.rawRules;
        if(rawRules){
            form.rawRules = rawRules;
        }

        // if there are virtual goods add them to the form
        var vGoods = config.getVirtualGoods();
        if(vGoods && vGoods.length > 0){
            form.vGoods = vGoods;
        }

        // if there are achievements add them to the form
        var achievements = config.getAchievements();
        if(achievements && achievements.length > 0){
            form.achievements = achievements;
        }

        // if there are segments add them to the form
        var segments = config.getSegments();
        if(segments && segments.length > 0){
            form.segments = segments;
        }

        return form;
    }
}
